import React from 'react';

import logo from '../../../img/logoDRONE-dark-cropped-final.png';
import './style.scss';

const PopUp = ({ toggleModal, setToggleModal, confirm, text, subtext }) => {
	return (
		<div className={`blurr-bg popup ${!toggleModal ? 'hide' : ''}`}>
			<div className='popup-content'>
				<div className='popup-heading'>
					<img className='popup-heading-img' src={logo} alt='logo' />
					<button
						type='button'
						onClick={() => {
							setToggleModal();
							confirm();
						}}
						className='btn popup-heading-btn'
					>
						<i className='fas fa-times'></i>
					</button>
				</div>
				<div className='popup-text'>
					<p>{text}</p>
					{subtext ? <p>{subtext}</p> : ''}
				</div>
				<div className='popup-btns'>
					<button
						type='button'
						className='btn btn-success'
						onClick={(e) => {
							e.preventDefault();
							confirm();
							setToggleModal();
						}}
					>
						OK
					</button>
					<button
						type='button'
						className='btn btn-danger'
						onClick={(e) => {
							e.preventDefault();
							setToggleModal();
						}}
					>
						Cancel
					</button>
				</div>
			</div>
		</div>
	);
};

export default PopUp;
