import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import Moment from 'react-moment';

import {
	loadReservations,
	deleteReservation,
} from '../../../actions/reservation';
import { loadUsers } from '../../../actions/user';
import { loadJobs } from '../../../actions/jobs';

import PopUp from '../../layouts/PopUp';
import Alert from '../../layouts/Alert';

import './ReservationsList.scss';

const ReservationsList = ({
	deleteReservation,
	loadReservations,
	loadUsers,
	loadJobs,
	user: { users, loading: loadingUsers },
	job: { jobs },
	reservation: { reservations, error, loading },
}) => {
	const [formData, setFormData] = useState({
		hourFrom: '',
		hourTo: '',
		user: '',
		job: '',
	});

	const [adminValues, setAdminValues] = useState({
		toggleDeleteConf: false,
		toggleUpdate: false,
		email: '',
		searchDisplay: false,
		results: [],
		showFilter: false,
		reservation: null,
	});

	const { hourFrom, hourTo, user, job } = formData;

	const {
		toggleDeleteConf,
		toggleUpdate,
		email,
		searchDisplay,
		results,
		showFilter,
		reservation,
	} = adminValues;

	useEffect(() => {
		if (loading) {
			loadJobs({}, true);
			loadReservations({ hourFrom: new Date() }, true);
		}
		if (!loadingUsers) {
			if (users.length > 0)
				setAdminValues((prev) => ({ ...prev, results: users }));
			else setAdminValues((prev) => ({ ...prev, results: [] }));
		}
	}, [loading, loadReservations, loadJobs, users, loadingUsers]);

	const onChange = (e) => {
		setFormData((prev) => ({
			...prev,
			[e.target.id]: e.target.value,
		}));
	};

	const selectUser = (user) => {
		setAdminValues((prev) => ({
			...prev,
			email: user.email,
			results: [],
			searchDisplay: false,
		}));
		setFormData((prev) => ({
			...prev,
			user: user.id,
		}));
	};

	const cancelUser = () => {
		setAdminValues((prev) => ({
			...prev,
			email: '',
		}));
		setFormData((prev) => ({
			...prev,
			user: '',
		}));
	};

	const onSubmit = (e) => {
		e.preventDefault();
		setFormData({
			hourFrom: '',
			hourTo: '',
			user: '',
			job: '',
		});
		loadReservations(formData, true);
	};

	return (
		<div className='reservations-list'>
			<PopUp
				type='confirmation'
				confirm={() => deleteReservation(reservation.id)}
				setToggleModal={() =>
					setAdminValues((prev) => ({
						...prev,
						toggleDeleteConf: !toggleDeleteConf,
					}))
				}
				toggleModal={toggleDeleteConf}
				text='Are you sure you want to delete the reservation?'
			/>
			<PopUp
				type='schedule'
				reservation={reservation}
				toggleModal={toggleUpdate}
				setToggleModal={() =>
					setAdminValues((prev) => ({
						...prev,
						toggleUpdate: !toggleUpdate,
					}))
				}
			/>
			<h2 className='heading-primary'>Reservations</h2>

			<Alert type='1' />
			<form className='form filter' onSubmit={onSubmit}>
				<p className='filter-title'>Filter</p>
				<button
					className='filter-icon'
					onClick={(e) => {
						e.preventDefault();
						setAdminValues((prev) => ({ ...prev, showFilter: !showFilter }));
					}}
				>
					{showFilter ? (
						<i className='fas fa-chevron-up'></i>
					) : (
						<i className='fas fa-chevron-down'></i>
					)}
				</button>
				<div className={`filter-content ${!showFilter ? 'hide' : ''}`}>
					<div className='form__group'>
						<div className='form__group-sub'>
							<div className='form__group-sub-item'>
								<input
									type='date'
									className='form__input'
									id='hourFrom'
									value={hourFrom}
									onChange={onChange}
									onFocus={() =>
										setAdminValues((prev) => ({
											...prev,
											searchDisplay: false,
										}))
									}
								/>
								<label htmlFor='hourFrom' className='form__label'>
									From
								</label>
							</div>
							<div className='form__group-sub-item'>
								<input
									type='date'
									className='form__input'
									id='hourTo'
									onChange={onChange}
									value={hourTo}
									onFocus={() =>
										setAdminValues((prev) => ({
											...prev,
											searchDisplay: false,
										}))
									}
								/>
								<label htmlFor='hourTo' className='form__label'>
									To
								</label>
							</div>
						</div>
					</div>

					<div className='form__group form-search'>
						<input
							className='form__input'
							type='text'
							value={email}
							disabled={user !== ''}
							name='email'
							id='email'
							autoComplete='off'
							onFocus={() =>
								setAdminValues((prev) => ({
									...prev,
									searchDisplay: true,
								}))
							}
							onChange={(e) => {
								setAdminValues((prev) => ({
									...prev,
									email: e.target.value,
								}));
								if (e.target.value.length > 1)
									loadUsers(
										{
											type: 'customer',
											email: e.target.value,
										},
										true
									);
							}}
							placeholder="User's Email"
						/>
						<label htmlFor='email' className='form__label'>
							User's Email
						</label>

						{searchDisplay && email.length > 1 && user === '' && (
							<ul
								className={`form-search-display ${
									results.length === 0 ? 'danger' : ''
								}`}
							>
								{results.length > 0 ? (
									results.map((user) => (
										<li
											className='form-search-item'
											onClick={() => selectUser(user)}
											key={user._id}
										>
											{user.email}
										</li>
									))
								) : (
									<li className='bg-danger form-search-item'>
										No matching results
									</li>
								)}
							</ul>
						)}
						{(user !== '' || (email.length > 1 && user === '')) && (
							<button
								type='button'
								onClick={cancelUser}
								className='form-search-close'
							>
								<i className='fas fa-times'></i>
							</button>
						)}
					</div>
					<div className='form__group'>
						<label htmlFor='job' className='form__label'>
							Select the Type of Job:
						</label>

						<select
							className='form__input'
							id='job'
							value={job}
							onChange={onChange}
							onFocus={() =>
								setAdminValues((prev) => ({
									...prev,
									searchDisplay: false,
								}))
							}
						>
							<option value=''>* Type of Job</option>
							{jobs.length > 0 &&
								jobs.map((job) => (
									<option key={job.id} value={job.id}>
										{job.title}
									</option>
								))}
						</select>
					</div>

					<button type='submit' className='btn btn-tertiary'>
						<i className='fas fa-search'></i>&nbsp; Search
					</button>
				</div>
			</form>
			{!loading && (
				<>
					{reservations.length > 0 ? (
						<div>
							<div className='wrapper'>
								<table className='stick icon-6'>
									<thead>
										<tr>
											<th>Date</th>
											<th>From</th>
											<th>To</th>
											<th>User</th>
											<th>Job</th>
											<th></th>
											<th></th>
										</tr>
									</thead>
									<tbody>
										{reservations.map((res) => (
											<tr key={res.id}>
												<td>
													<Moment date={res.hourFrom} utc format='MM/DD/YY' />
												</td>
												<td>
													<Moment date={res.hourFrom} utc format='h a' />
												</td>
												<td>
													<Moment date={res.hourTo} utc format='h a' />
												</td>
												<td>
													<Link
														className='btn-link text-dark'
														to={`/edit-user/${res.user.id}`}
													>{`${res.user.name} ${res.user.lastname}`}</Link>
												</td>
												<td>{res.job.title}</td>
												<td>
													<button
														className='btn-icon'
														onClick={() => {
															window.scroll(0, 0);
														}}
													>
														<i className='far fa-edit'></i>
													</button>
												</td>
												<td>
													<button
														className='btn-icon'
														onClick={() =>
															setAdminValues((prev) => ({
																...prev,
																toggleDeleteConf: !toggleDeleteConf,
																reservation: res,
															}))
														}
													>
														<i className='far fa-trash-alt'></i>
													</button>
												</td>
											</tr>
										))}
									</tbody>
								</table>
							</div>
							<div className='reservations-list-total'>
								<span className='reservations-list-total-title text-dark'>
									Total:
								</span>
								&nbsp;
								{reservations.length}
							</div>
						</div>
					) : (
						<h3 className='heading-primary-subheading u-center-text text-danger'>
							{error.msg}
						</h3>
					)}
					<div className='btn-right'>
						<button
							onClick={() =>
								setAdminValues((prev) => ({
									...prev,
									toggleUpdate: !toggleUpdate,
								}))
							}
							className='btn btn-primary'
						>
							<i className='fas fa-plus'></i> &nbsp; Reservation
						</button>
					</div>
				</>
			)}
		</div>
	);
};

const mapStateToProps = (state) => ({
	reservation: state.reservation,
	user: state.user,
	job: state.job,
});

export default connect(mapStateToProps, {
	loadReservations,
	deleteReservation,
	loadUsers,
	loadJobs,
})(ReservationsList);
