import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import Moment from 'react-moment';

import {
	loadReservations,
	deleteReservation,
} from '../../../actions/reservation';
import { loadUsers } from '../../../actions/user';

import PopUp from '../../layouts/PopUp';
import Alert from '../../Alert';

const ReservationList = ({
	deleteReservation,
	loadReservations,
	loadUsers,
	user: { users, loading: loadingUsers },
	reservation: { reservations, error, loading },
}) => {
	const [formData, setFormData] = useState({
		hourFrom: '',
		hourTo: '',
		user: '',
		job: '',
	});

	const [adminValues, setAdminValues] = useState({
		toggleDeleteConf: false,
		toDelete: '',
	});

	const { hourFrom, hourTo, user, job } = formData;

	const { toggleDeleteConf, toDelete } = adminValues;

	useEffect(() => {
		if (loading) loadReservations({ hourFrom: new Date() });
	}, [loading, loadReservations]);

	const onChange = (e) => {
		setFormData((prev) => ({
			...prev,
			[e.target.id]: e.target.value,
		}));
	};

	const onSubmit = (e) => {
		e.preventDefault();
		loadReservations(formData);
	};

	return (
		<div className='reservations-list'>
			<PopUp
				type='confirmation'
				confirm={() => deleteReservation(toDelete)}
				setToggleModal={() =>
					setAdminValues((prev) => ({
						...prev,
						toggleDeleteConf: !toggleDeleteConf,
					}))
				}
				toggleModal={toggleDeleteConf}
				text='Are you sure you want to delete the reservation?'
			/>
			<h2 className='heading-primary'>Reservations</h2>

			<Alert type='1' />
			<form className='form' onSubmit={onSubmit}>
				<p className=''>Filter</p>
				<div className='form__group'>
					<div className='form__group-sub'>
						<div className='form__group-sub-item'>
							<input
								type='date'
								className='form__input'
								id='hourFrom'
								value={hourFrom}
							/>
							<label htmlFor='hourFrom' className='form__label'>
								From
							</label>
						</div>
						<div className='form__group-sub-item'>
							<input
								type='date'
								className='form__input'
								id='hourTo'
								value={hourTo}
							/>
							<label htmlFor='hourTo' className='form__label'>
								To
							</label>
						</div>
					</div>
				</div>
				<div className='form__group'>
					<label htmlFor='job' className='form__label'>
						Select the Type of Job:
					</label>

					<select
						className='form__input'
						id='job'
						value={job}
						onChange={(e) =>
							setFormData((prev) => ({ ...prev, job: e.target.value }))
						}
					>
						<option value=''>* Type of Job</option>
						{jobs.length > 0 &&
							jobs.map((job, i) => (
								<option key={job.id} value={i}>
									{job.title}
								</option>
							))}
					</select>
				</div>

				<div className='u-center-text'>
					<button type='submit' className='btn btn-primary'>
						<i className='fas fa-search'></i>
					</button>
				</div>
			</form>
			{!loading && (
				<>
					{reservations.length > 0 ? (
						<div className='wrapper'>
							<table className='stick icon-6'>
								<thead>
									<tr>
										<th>Date</th>
										<th>From</th>
										<th>To</th>
										<th>User</th>
										<th>Job</th>
										<th></th>
										<th></th>
									</tr>
								</thead>
								<tbody>
									{reservations.map((reservation) => (
										<tr key={reservation.id}>
											<td>
												<Moment
													date={reservation.hourFrom}
													utc
													format='MM/DD/YY'
												/>
											</td>
											<td>
												<Moment date={reservation.hourFrom} utc format='h a' />
											</td>
											<td>
												<Moment date={reservation.hourTo} utc format='h a' />
											</td>
											<td>
												<Link
													to={`/edit-user/${reservation.user.id}`}
												>{`${reservation.user.name} ${reservation.user.lastname}`}</Link>
											</td>
											<td>{reservation.job.title}</td>
											<td>
												<button
													className='btn-icon'
													onClick={() => {
														window.scroll(0, 0);
													}}
												>
													<i className='far fa-edit'></i>
												</button>
											</td>
											<td>
												<button
													className='btn-icon'
													onClick={() =>
														setAdminValues((prev) => ({
															...prev,
															toggleDeleteConf: !toggleDeleteConf,
															toDelete: reservation.id,
														}))
													}
												>
													<i className='far fa-trash-alt'></i>
												</button>
											</td>
										</tr>
									))}
								</tbody>
							</table>
						</div>
					) : (
						<h3 className='heading-primary'>{error.msg}</h3>
					)}
					<div className='btn-right'>
						<Link to='/new-reservation' className='btn btn-primary'>
							<i className='fas fa-plus'></i>Reservation
						</Link>
					</div>
				</>
			)}
		</div>
	);
};

const mapStateToProps = (state) => ({
	reservation: state.reservation,
	user: state.user,
});

export default connect(mapStateToProps, {
	loadReservations,
	deleteReservation,
	loadUsers,
})(ReservationList);
