import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';

import { loadJobs } from '../../../actions/jobs';
import { loadReservations } from '../../../actions/reservation';

import Alert from '../../Alert';

import './Reservation.scss';

const Reservation = ({
	auth: { loggedUser },
	reservation: { reservations, loading: loadingReservations },
	job: { jobs, loading },
	loadJobs,
	loadReservations,
}) => {
	const [formData, setFormData] = useState({
		hourFrom: '',
		hourTo: '',
		value: '',
		jobId: '',
		userId: '',
	});

	const { hourFrom, hourTo, jobId, userId } = formData;

	useEffect(() => {
		if (loading) loadJobs({});
		if (loggedUser && loggedUser.type !== 'admin' && loadingReservations)
			loadReservations({ hourFrom: new Date(), user: loggedUser.id });
	}, [loading, loadJobs, loggedUser, loadReservations, loadingReservations]);

	return (
		<div className='reservation'>
			<h2 className='heading-primary'>Reservations</h2>
			<Alert type='1' />
			{loggedUser && (
				<h5 className='heading-primary-subheading'>
					You need to be logged in to make a reservation
				</h5>
			)}
		</div>
	);
};

const mapStateToProps = (state) => ({
	auth: state.auth,
	job: state.job,
	reservation: state.reservation,
});

export default connect(mapStateToProps, { loadJobs, loadReservations })(
	Reservation
);
