import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';

import { setAlert } from '../../../actions/alert';
import { loadJobs } from '../../../actions/jobs';
import { loadReservations } from '../../../actions/reservation';

import Alert from '../../Alert';

import './Reservation.scss';

const Reservation = ({
	auth: { loggedUser },
	reservation: { reservations, loading: loadingReservations },
	job: { jobs, loading },
	loadJobs,
	loadReservations,
}) => {
	const today = new Date();

	useEffect(() => {
		if (loading) loadJobs({});
		if (loggedUser && loggedUser.type !== 'admin' && loadingReservations)
			loadReservations({ hourFrom: today, user: loggedUser.id });
	}, [loading, loadJobs, loggedUser, today, loadReservations]);

	return (
		<div className='reservation'>
			<h2 className='heading-primary'>Reservations</h2>
			{loggedUser && (
				<h5 className='heading-primary-subheading'>
					You need to be logged in to make a reservation
				</h5>
			)}
		</div>
	);
};

const mapStateToProps = (state) => ({
	auth: state.auth,
	job: state.job,
	reservation: state.reservation,
});

export default connect(mapStateToProps, { loadJobs, loadReservations })(
	Reservation
);
