import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

import { logOut } from '../../../actions/auth';
import { clearReservations } from '../../../actions/reservation';
import { clearJobs } from '../../../actions/jobs';

import './Navbar.scss';

const Navbar = ({
	logOut,
	clearReservations,
	clearJobs,
	auth: { loggedUser, loading },
}) => {
	const [click, setClick] = useState(false);
	const [button, setButton] = useState(true);

	const handleClick = () => setClick(!click);
	const closeMobileMenu = () => setClick(false);

	useEffect(() => {
		showButton();
	}, []);

	const showButton = () => {
		if (window.innerWidth <= 960) {
			setButton(false);
		} else {
			setButton(true);
		}
	};

	window.addEventListener('resize', showButton);

	return (
		<nav className='navbar' id='top'>
			<div className='navbar-container'>
				<div className='menu-icon' onClick={handleClick}>
					<i className={click ? 'fas fa-times' : 'fas fa-bars'}></i>
				</div>

				<ul className={click ? 'nav-menu active' : 'nav-menu'}>
					{!loading && loggedUser && (
						<li className='nav-links-salute'>
							<Link
								to='/profile'
								onClick={() => {
									closeMobileMenu();
									window.scrollTo(0, 0);
								}}
								className='btn-link'
							>
								Hi {loggedUser.name}!
							</Link>
						</li>
					)}
					{!loading && loggedUser.type === 'admin' ? (
						<>
							<li className='nav-item'>
								<Link
									to='/dashboard'
									className='nav-links'
									onClick={() => {
										closeMobileMenu();
										window.scrollTo(0, 0);
									}}
								>
									Home
								</Link>
							</li>
							<li className='nav-item'>
								<Link
									to='/reservations-list'
									className='nav-links'
									onClick={() => {
										closeMobileMenu();
										window.scrollTo(0, 0);
										if (!loading && loggedUser) {
											clearReservations();
											clearJobs();
										}
									}}
								>
									Reservations
								</Link>
							</li>
						</>
					) : (
						<>
							<li className='nav-item'>
								<Link
									to='/'
									className='nav-links'
									onClick={() => {
										closeMobileMenu();
										window.scrollTo(0, 0);
									}}
								>
									Home
								</Link>
							</li>
							<li className='nav-item'>
								<Link
									to='/servicesfull'
									className='nav-links'
									onClick={() => {
										closeMobileMenu();
										clearJobs();
										window.scrollTo(0, 0);
									}}
								>
									Services
								</Link>
							</li>
							<li
								className='nav-item'
								style={!loading && loggedUser ? { order: 5 } : null}
							>
								<Link
									to='/portfolio'
									className='nav-links'
									onClick={() => {
										closeMobileMenu();
										window.scrollTo(0, 0);
									}}
								>
									Portfolio
								</Link>
							</li>
							<li
								className='nav-item'
								style={!loading && loggedUser ? { order: 4 } : null}
							>
								<Link
									to={!loading && loggedUser ? '/reservation/0' : '/contact'}
									className='nav-links'
									onClick={() => {
										closeMobileMenu();
										window.scrollTo(0, 0);
										if (!loading && loggedUser) {
											clearReservations();
											clearJobs();
										}
									}}
								>
									{!loading && loggedUser ? 'Reservations' : 'Contact'}
								</Link>
							</li>
						</>
					)}

					<li
						className='nav-item'
						style={!loading && loggedUser ? { order: 6 } : null}
					>
						<Link
							to={!loading && loggedUser ? '/' : '/login'}
							onClick={() => {
								if (!loading && loggedUser) logOut();
								window.scrollTo(0, 0);
							}}
							className='nav-links-mobile btn btn-outline'
						>
							{!loading && loggedUser ? 'Log Out' : 'Log In'}
						</Link>
					</li>
				</ul>
			</div>
			{!loading && loggedUser && (
				<div className='nav-salute'>
					<Link
						to='/profile'
						onClick={() => window.scrollTo(0, 0)}
						className='btn-link'
					>
						Hi {loggedUser.name}!
					</Link>
				</div>
			)}
			<div className='nav-button'>
				{button && (
					<Link
						to={!loading && loggedUser ? '/' : '/login'}
						onClick={() => {
							if (!loading && loggedUser) logOut();
							window.scrollTo(0, 0);
						}}
						className='btn btn-outline'
					>
						{!loading && loggedUser ? 'Log Out' : 'Log In'}
					</Link>
				)}
			</div>
		</nav>
	);
};

const mapStateToProps = (state) => ({
	auth: state.auth,
});

export default connect(mapStateToProps, {
	logOut,
	clearReservations,
	clearJobs,
})(Navbar);
