import React, { useState } from 'react';

import logo from '../../../img/logoDRONE-dark-cropped-final.png';
import './PopUp.scss';

const PopUp = ({
	toggleModal,
	setToggleModal,
	confirm,
	text,
	subtext,
	type,
	res,
	jobs,
}) => {
	const [resInfo, setResInfo] = useState({
		reservation: res ? res : null,
		resType: res ? 'update' : 'reserve',
	});

	const { reservation, resType, job } = resInfo;

	const selectType = () => {
		switch (type) {
			case 'confirmation':
				return (
					<div className='popup-text'>
						<p>{text}</p>
						{subtext ? <p>{subtext}</p> : ''}
					</div>
				);
			case 'schedule':
				return (
					<ReservationForm
						type={resType}
						reservation={reservation}
						complete={setToggleModal}
					/>
				);
			default:
				break;
		}
	};

	return (
		<div className={`blurr-bg popup ${!toggleModal ? 'hide' : ''}`}>
			<div className={`popup-content ${type}`}>
				<div className='popup-heading'>
					<img className='popup-heading-img' src={logo} alt='logo' />
					<button
						type='button'
						onClick={setToggleModal}
						className='popup-heading-btn'
					>
						<i className='fas fa-times'></i>
					</button>
				</div>
				{selectType()}
				<div className='popup-btns'>
					<button
						type='button'
						className='btn btn-success'
						onClick={(e) => {
							e.preventDefault();
							confirm();
							setToggleModal();
						}}
					>
						OK
					</button>
					<button
						type='button'
						className='btn btn-danger'
						onClick={setToggleModal}
					>
						Cancel
					</button>
				</div>
			</div>
		</div>
	);
};

export default PopUp;
