import React, { useState, useEffect } from 'react';
import Moment from 'react-moment';
import moment from 'moment';
import Calendar from 'react-calendar';
import { connect } from 'react-redux';

import {
	checkDayAvailability,
	checkMonthAvailability,
} from '../../actions/day';
import {
	updateReservation,
	registerReservation,
} from '../../actions/reservation';

import Alert from '../Alert';

const Schedule = ({
	job,
	auth: { loggedUser },
	reservations: { reservation },
	days: { availableHours, loadingAvailableHours, disabledDays },
	checkMonthAvailability,
	checkDayAvailability,
	/* updateReservation, */
	registerReservation,
}) => {
	const today = new Date();
	const type = reservation ? 'update' : 'reserve';

	const [formData, setFormData] = useState({
		hourFrom: '',
		hourTo: '',
		value: job.price,
		jobId: job.id,
		userId: loggedUser.id,
	});

	const { hourFrom, hourTo, value } = formData;

	const [adminValues, setAdminValues] = useState({
		date: new Date(),
		tab: 0,
		toggleModal: false,
	});

	const { date, tab, toggleModal } = adminValues;

	useEffect(() => {
		const today = new Date();

		checkMonthAvailability(job.id, today.getMonth(), today.getFullYear());
	}, [checkMonthAvailability, job.id]);

	const onChangeDate = (changedDate) => {
		setAdminValues((prev) => ({
			...prev,
			date: changedDate,
			tab: 1,
		}));
		checkDayAvailability(
			moment(changedDate).format('YYYY-MM-DD[T00:00:00Z]'),
			job.id
		);
	};

	const selectHourFrom = (time) => {
		const newDate = new Date(date);
		newDate.setHours(time);

		let newToDate;

		newToDate = new Date(newDate);
		newToDate.setHours(newDate.getHours() + job.time);

		setFormData((prev) => ({
			...prev,
			hourFrom: newDate,
			hourTo: newToDate,
		}));

		setAdminValues((prev) => ({
			...prev,
			tab: 2,
		}));
	};

	/* const getToTime = (item) => {
		const time = hourFrom.getHours() + vessel.prices[item].time;
		if (time > 24) return time - 24 + ' am';
		return `${time % 12 !== 0 ? time % 12 : 12} ${
			time >= 12 && time !== 24 ? 'pm' : 'am'
		}`;
	}; */

	const tileDisabled = ({ date, view }) => {
		if (view === 'month' && disabledDays.length > 0) {
			// Check if a date React-Calendar wants to check is on the list of disabled dates
			return disabledDays.find((dDate) => isSameDay(dDate, date));
		}
	};

	const isSameDay = (date1, date2) => {
		if (
			moment(date1.substring(0, date1.length - 2)).format('MM-DD-YYYY') ===
			moment(date2).format('MM-DD-YYYY')
		)
			return true;
		return false;
	};

	const tabOpen = () => {
		switch (tab) {
			case 0:
				return (
					<>
						<h5 className='heading-primary-subheading'>Pick up a Date</h5>
					</>
				);
			case 1:
				return (
					!loadingAvailableHours && (
						<>
							{availableHours.length > 0 ? (
								<>
									<p className='schedule-title'>From:</p>
									{availableHours.map((hour, i) => (
										<div
											className='schedule-item'
											key={i}
											onClick={() => selectHourFrom(hour)}
										>
											{`${hour % 12 !== 0 ? hour % 12 : 12} ${
												hour >= 12 ? 'pm' : 'am'
											}`}
										</div>
									))}
								</>
							) : (
								<h2 className='schedule-warning'>
									No availability on this day
								</h2>
							)}
						</>
					)
				);
			case 2:
				return (
					<>
						<p className='schedule-title'>
							{type === 'update' ? 'New' : 'Reservation'} Info:
						</p>
						<form
							className='form'
							onSubmit={(e) => {
								e.preventDefault();

								if (type === 'update') {
									setAdminValues((prev) => ({
										...prev,
										toggleModal: !toggleModal,
									}));
								} else {
									registerReservation({
										...formData,
										hourFrom: moment(hourFrom).format(
											'YYYY-MM-DD[T]HH:mm:SS[Z]'
										),
										hourTo: moment(hourTo).format('YYYY-MM-DD[T]HH:mm:SS[Z]'),
									});
								}
							}}
						>
							<Alert type='2' />
							<p className='schedule-info'>
								<span className='schedule-subtitle'>Date</span> &nbsp;
								<Moment date={hourFrom} format='MM/DD/YY' />
							</p>
							<p className='schedule-info'>
								<span className='schedule-subtitle'>Time:</span> &nbsp;
								<Moment format='h a' date={hourFrom} /> -{' '}
								<Moment format='h a' date={hourTo} />
							</p>
							{type === 'reserve' && (
								<p className='schedule-info'>
									<span className='text-primary'>Price:</span> ${value}
								</p>
							)}

							<div className='u-center-text'>
								<button type='submit' className='btn btn-primary'>
									{type === 'update'
										? <i className='far fa-save'></i> + ' Update'
										: 'Reserve Now!'}
								</button>
							</div>
						</form>
					</>
				);
			default:
				break;
		}
	};

	return (
		<div className='schedule'>
			{/* {type === 'update' && (
				<PopUp
					toggleModal={toggleModal}
					setToggleModal={() =>
						setAdminValues((prev) => ({
							...prev,
							toggleModal: !toggleModal,
						}))
					}
					confirm={() =>
						updateReservation(
							{
								hourFrom: moment(hourFrom).format('YYYY-MM-DD[T]HH:mm:SS[Z]'),
								hourTo: moment(hourTo).format('YYYY-MM-DD[T]HH:mm:SS[Z]'),
							},
							reservation.id,
							loggedUser.type
						)
					}
					text='Are you sure you want to modify the reservation?'
					type='confirmation'
				/>
			)} */}

			<div className='row-item'>
				<div className='my-3'>
					<Calendar
						value={date}
						onChange={onChangeDate}
						minDate={today}
						maxDate={
							new Date(
								today.getFullYear() + 1,
								today.getMonth(),
								today.getDate()
							)
						}
						onActiveStartDateChange={(e) => {
							if (e.view === 'month') {
								checkMonthAvailability(
									job.id,
									e.activeStartDate.getMonth(),
									e.activeStartDate.getFullYear()
								);
							}
						}}
						tileDisabled={tileDisabled}
					/>
				</div>
			</div>
			<div className='row-item'>{tabOpen()}</div>
		</div>
	);
};

const mapStateToProps = (state) => ({
	auth: state.auth,
	days: state.days,
	reservations: state.reservations,
});

export default connect(mapStateToProps, {
	checkDayAvailability,
	checkMonthAvailability,
	updateReservation,
	registerReservation,
})(Schedule);
