import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import Moment from 'react-moment';

import { loadJobs } from '../../../actions/jobs';
import {
	loadReservations,
	deleteReservation,
} from '../../../actions/reservation';

import Alert from '../../Alert';
import Loading from '../../layouts/Loading';
import PopUp from '../../layouts/PopUp';
import Schedule from '../../Schedule';

import './Reservation.scss';

const Reservation = ({
	auth: { loggedUser, token },
	reservation: { reservations, loading: loadingReservations },
	job: { jobs, loading },
	loadJobs,
	loadReservations,
	deleteReservation,
}) => {
	const [adminValues, setAdminValues] = useState({
		job: '',
		type: 'reserve',
		reservation: {
			id: '',
		},
		toggleModal: false,
	});

	const { job, type, reservation, toggleModal } = adminValues;

	useEffect(() => {
		if (loading) loadJobs({}, token ? true : false);
		if (loggedUser && loggedUser.type !== 'admin' && loadingReservations)
			loadReservations({ hourFrom: new Date(), user: loggedUser.id }, true);
	}, [
		loading,
		loadJobs,
		loggedUser,
		loadReservations,
		loadingReservations,
		token,
	]);

	return (
		<div className='reservation'>
			<Loading />
			<h2 className='heading-primary'>Reservations</h2>
			<PopUp
				toggleModal={toggleModal}
				setToggleModal={() =>
					setAdminValues((prev) => ({
						...prev,
						toggleModal: !toggleModal,
					}))
				}
				confirm={() => {
					deleteReservation(reservation.id);
				}}
				text='Are you sure you want to delete the reservation?'
			/>
			<Alert type='1' />
			{!loggedUser ? (
				<h5 className='heading-primary-subheading error'>
					You need to be logged in to make a reservation. &nbsp;
					<Link to='/login' className='btn-link'>
						Login
					</Link>
				</h5>
			) : (
				loggedUser.cel === '0' && (
					<h5 className='heading-primary-subheading error'>
						You need to save your cellphone in your profile to make a
						reservation. &nbsp;
						<Link to='/profile' className='btn-link'>
							Profile
						</Link>
					</h5>
				)
			)}

			<div className='reservation-list'>
				{!loadingReservations && reservations.length > 0 ? (
					reservations.map((res) => (
						<div className='reservation-item' key={res.id}>
							<div className='reservation-item-date'>
								<Moment
									date={res.hourFrom}
									className='date'
									format='MM-DD-YY'
								/>
								<Moment date={res.hourFrom} utc format='h a' /> -{' '}
								<Moment date={res.hourTo} utc format='h a' />
							</div>
							<div className='reservation-item-job'>
								<p>
									<span className='reservation-item-title'>Job: </span>{' '}
									{res.job.title}
								</p>
								<p>
									<span className='reservation-item-title'>Value: </span> $
									{res.value}
								</p>
							</div>
							<div className='reservation-item-icons'>
								<button
									onClick={() => {
										let job = jobs.findIndex((item) => item.id === res.jobId);
										setAdminValues((prev) => ({
											...prev,
											reservation: res,
											job,
											type: 'update',
										}));
									}}
								>
									<i className='far fa-edit'></i>
								</button>
								<button
									onClick={() =>
										setAdminValues((prev) => ({
											...prev,
											toggleModal: !toggleModal,
											reservation: res,
										}))
									}
								>
									<i className='far fa-trash-alt'></i>
								</button>
							</div>
						</div>
					))
				) : (
					<h2 className='heading-primary-subheading'>No Reservations Made</h2>
				)}
			</div>
			<div className='reservation-form'>
				{!loading && (
					<div className='reservation-form__group'>
						<h4 className='heading-primary-subheading'>
							{type === 'update' ? 'Update' : 'New'} Reservation
						</h4>
						{type === 'update' && (
							<p className='heading-primary-subheading-update'>
								<Moment
									date={reservation.hourFrom}
									className='date'
									format='MM-DD-YY'
								/>{' '}
								&nbsp;
								<Moment date={reservation.hourFrom} utc format='h a' /> -{' '}
								<Moment date={reservation.hourTo} utc format='h a' />
							</p>
						)}
						<div className='form__group'>
							{type !== 'update' && (
								<label htmlFor='job' className='form__label'>
									Select the Type of Job for the New Reservation:
								</label>
							)}

							<select
								className='form__input'
								id='job'
								value={job}
								disabled={type === 'update'}
								onChange={(e) =>
									setAdminValues((prev) => ({ ...prev, job: e.target.value }))
								}
							>
								<option value=''>* Type of Job</option>
								{jobs.length > 0 &&
									jobs.map((job, i) => (
										<option key={job.id} value={i}>
											{job.title}
										</option>
									))}
							</select>
						</div>
					</div>
				)}
				{job !== '' && (
					<Schedule
						job={jobs[job]}
						complete={() =>
							setAdminValues((prev) => ({ ...prev, job: '', type: 'reserve' }))
						}
						type={type}
						reservation={reservation.id}
					/>
				)}
			</div>
		</div>
	);
};

const mapStateToProps = (state) => ({
	auth: state.auth,
	job: state.job,
	reservation: state.reservation,
});

export default connect(mapStateToProps, {
	loadJobs,
	loadReservations,
	deleteReservation,
})(Reservation);
