import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';

import { logOut } from '../../../actions/auth';

import './Navbar.scss';

const Navbar = ({ logOut, auth: { loggedUser, loading } }) => {
	const [click, setClick] = useState(false);
	const [button, setButton] = useState(true);

	const handleClick = () => setClick(!click);
	const closeMobileMenu = () => setClick(false);

	useEffect(() => {
		showButton();
	}, []);

	const showButton = () => {
		if (window.innerWidth <= 960) {
			setButton(false);
		} else {
			setButton(true);
		}
	};

	window.addEventListener('resize', showButton);

	return (
		<nav className='navbar' id='top'>
			<div className='navbar-container'>
				<div className='menu-icon' onClick={handleClick}>
					<i className={click ? 'fas fa-times' : 'fas fa-bars'}></i>
				</div>
				<ul className={click ? 'nav-menu active' : 'nav-menu'}>
					<li className='nav-item'>
						<Link to='/' className='nav-links' onClick={closeMobileMenu}>
							Home
						</Link>
					</li>
					<li className='nav-item'>
						<Link
							to='/servicesfull'
							className='nav-links'
							onClick={closeMobileMenu}
						>
							Services
						</Link>
					</li>
					<li
						className='nav-item'
						style={!loading && loggedUser ? { order: 4 } : null}
					>
						<Link
							to='/portfolio'
							className='nav-links'
							onClick={closeMobileMenu}
						>
							Portfolio
						</Link>
					</li>
					<li
						className='nav-item'
						style={!loading && loggedUser ? { order: 3 } : null}
					>
						{!loading && loggedUser ? (
							<Link
								to='/reservation'
								className='nav-links'
								onClick={closeMobileMenu}
							>
								Reservations
							</Link>
						) : (
							<Link
								to='/contact'
								className='nav-links'
								onClick={closeMobileMenu}
							>
								Contact
							</Link>
						)}
					</li>
					<li
						className='nav-item'
						style={!loading && loggedUser ? { order: 5 } : null}
					>
						<Link
							to={!loading && loggedUser ? '/' : '/login'}
							onClick={() => {
								if (!loading && loggedUser) logOut();
							}}
							className='btn btn-outline'
						>
							{!loading && loggedUser ? 'Log Out' : 'Log In'}
						</Link>
					</li>
				</ul>
			</div>
			{!loading && loggedUser && (
				<div className='nav-salute'>
					<Link to='/profile' className='btn-link'>
						Hi {loggedUser.name}!
					</Link>
				</div>
			)}
			<div className='nav-button'>
				{button && (
					<Link
						to={!loading && loggedUser ? '/' : '/login'}
						onClick={() => {
							if (!loading && loggedUser) logOut();
						}}
						className='btn btn-outline'
					>
						{!loading && loggedUser ? 'Log Out' : 'Log In'}
					</Link>
				)}
			</div>
		</nav>
	);
};

const mapStateToProps = (state) => ({
	auth: state.auth,
});

export default connect(mapStateToProps, { logOut })(Navbar);
