import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import Moment from 'react-moment';

import { loadJobs } from '../../../actions/jobs';
import {
	loadReservations,
	deleteReservation,
} from '../../../actions/reservation';

import Alert from '../../layouts/Alert';
import Loading from '../../layouts/Loading';
import PopUp from '../../layouts/PopUp';

import './Reservation.scss';
import ReservationForm from '../../ReservationForm';

const Reservation = ({
	auth: { loggedUser, token },
	reservation: { reservations, loading: loadingReservations },
	job: { jobs, loading },
	loadJobs,
	loadReservations,
	deleteReservation,
	match,
}) => {
	const [adminValues, setAdminValues] = useState({
		type: 'reserve',
		reservation: {
			id: '',
		},
		toggleModal: false,
	});

	const { type, reservation, toggleModal } = adminValues;

	useEffect(() => {
		if (loading) loadJobs({}, token ? true : false);
		else {
			if (match.params.job_id !== '0')
				setAdminValues((prev) => ({ ...prev, job: match.params.job_id }));
		}
		if (loggedUser && loggedUser.type !== 'admin' && loadingReservations)
			loadReservations({ hourFrom: new Date(), user: loggedUser.id }, true);
	}, [
		loading,
		loadJobs,
		loggedUser,
		loadReservations,
		loadingReservations,
		token,
		match.params.job_id,
	]);

	return (
		<div className='reservation'>
			<Loading />
			<h2 className='heading-primary'>Reservations</h2>
			<PopUp
				type='confirmation'
				toggleModal={toggleModal}
				setToggleModal={() =>
					setAdminValues((prev) => ({
						...prev,
						toggleModal: !toggleModal,
					}))
				}
				confirm={() => {
					deleteReservation(reservation.id);
				}}
				text='Are you sure you want to delete the reservation?'
			/>
			<Alert type='1' />
			{!loggedUser ? (
				<h5 className='heading-primary-subheading error'>
					You need to be logged in to make a reservation. &nbsp;
					<Link
						to='/login'
						onClick={() => window.scrollTo(0, 0)}
						className='btn-link'
					>
						Login
					</Link>
				</h5>
			) : (
				loggedUser.cel === '0' && (
					<h5 className='heading-primary-subheading error'>
						You need to save your cellphone in your profile to make a
						reservation. &nbsp;
						<Link
							to='/profile'
							onClick={() => window.scrollTo(0, 0)}
							className='btn-link'
						>
							Profile
						</Link>
					</h5>
				)
			)}

			<div className='reservation-list'>
				{!loadingReservations && reservations.length > 0 ? (
					reservations.map((res) => (
						<div className='reservation-item' key={res.id}>
							<div className='reservation-item-date'>
								<Moment
									date={res.hourFrom}
									className='date'
									format='MM-DD-YY'
								/>
								<Moment date={res.hourFrom} utc format='h a' /> -{' '}
								<Moment date={res.hourTo} utc format='h a' />
							</div>
							<div className='reservation-item-job'>
								<p>
									<span className='reservation-item-title'>Job: </span>{' '}
									{res.job.title}
								</p>
								<p>
									<span className='reservation-item-title'>Value: </span> $
									{res.value}
								</p>
							</div>
							<div className='reservation-item-icons'>
								<button
									onClick={() => {
										let job = jobs.findIndex((item) => item.id === res.jobId);
										setAdminValues((prev) => ({
											...prev,
											reservation: res,
											job,
											type: 'update',
										}));
									}}
									className='btn-icon'
								>
									<i className='far fa-edit'></i>
								</button>
								<button
									onClick={() =>
										setAdminValues((prev) => ({
											...prev,
											toggleModal: !toggleModal,
											reservation: res,
										}))
									}
									className='btn-icon'
								>
									<i className='far fa-trash-alt'></i>
								</button>
							</div>
						</div>
					))
				) : (
					<h2 className='heading-primary-subheading'>No Reservations Made</h2>
				)}
			</div>
			{type === 'update' && (
				<button className='reservation-cancel'>
					<i className='fas fa-times'></i>
				</button>
			)}
			<ReservationForm
				type={type}
				jobs={jobs}
				reservation={reservation}
				complete={() =>
					setAdminValues((prev) => ({ ...prev, type: 'reserve' }))
				}
			/>
		</div>
	);
};

const mapStateToProps = (state) => ({
	auth: state.auth,
	job: state.job,
	reservation: state.reservation,
});

export default connect(mapStateToProps, {
	loadJobs,
	loadReservations,
	deleteReservation,
})(Reservation);
