import React, { useEffect } from 'react';
import { connect } from 'react-redux';

import { activation } from '../../../actions/auth';

import './Activation.scss';

const Activation = ({
	match,
	auth: { loading, loggedUser, error },
	activation,
}) => {
	const token = match.params.token;

	useEffect(() => {
		activation(token);
	}, [token, activation]);

	return (
		!loading && (
			<div className='activation'>
				{error !== '' ? (
					<>
						<h2 className='heading-primary'>Ups... something went wrong!</h2>
						<p className='heading-tertiary m-3 py-3 text-danger'>{error.msg}</p>
					</>
				) : (
					loggedUser !== null && (
						<>
							<h2 className='heading-secondary-gradient'>
								Welcome {loggedUser.name + ' ' + loggedUser.lastname}!
							</h2>
							<p className='heading-tertiary m-3 py-3'>
								Welcome to Drone Tahoe! You can start using our services now.
							</p>
						</>
					)
				)}
			</div>
		)
	);
};

const mapStateToProps = (state) => ({
	auth: state.auth,
});

export default connect(mapStateToProps, { activation })(Activation);
