import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';

import { loadJobs } from '../../../actions/jobs';
import { loadReservations } from '../../../actions/reservation';
import { updateLoadingSpinner } from '../../../actions/global';

import Alert from '../../Alert';
import Loading from '../../layouts/Loading';
import Schedule from '../../Schedule';

import './Reservation.scss';

const Reservation = ({
	auth: { loggedUser, token },
	reservation: { reservations, loading: loadingReservations },
	job: { jobs, loading },
	loadJobs,
	loadReservations,
	updateLoadingSpinner,
}) => {
	const [job, setJob] = useState('');

	useEffect(() => {
		if (loading) loadJobs({}, token ? true : false);
		if (loggedUser && loggedUser.type !== 'admin' && loadingReservations)
			loadReservations({ hourFrom: new Date(), user: loggedUser.id }, true);
		//else updateLoadingSpinner(false);
	}, [
		loading,
		loadJobs,
		loggedUser,
		loadReservations,
		loadingReservations,
		//updateLoadingSpinner,
	]);

	return (
		<div className='reservation'>
			<Loading />
			<h2 className='heading-primary'>Reservations</h2>
			{!loggedUser ? (
				<h5 className='heading-primary-subheading'>
					You need to be logged in to make a reservation. &nbsp;
					<Link to='/login' className='btn-link'>
						Login
					</Link>
				</h5>
			) : (
				loggedUser.cel === '0' && (
					<h5 className='heading-primary-subheading'>
						You need to save your cellphone in your profile to make a
						reservation. &nbsp;
						<Link to='/profile' className='btn-link'>
							Profile
						</Link>
					</h5>
				)
			)}
			<div className='reservation-form'>
				<Alert type='1' />
				{!loading && (
					<div className='reservation-form__group'>
						<div className='form__group'>
							<label htmlFor='job' className='form__label'>
								Select the Type of Job for the Reservation:
							</label>
							<select
								className='form__input'
								id='job'
								value={job}
								onChange={(e) => setJob(e.target.value)}
							>
								<option value=''>* Type of Job</option>
								{jobs.length > 0 &&
									jobs.map((job, i) => (
										<option key={job.id} value={i}>
											{job.title}
										</option>
									))}
							</select>
						</div>
					</div>
				)}

				{job !== '' && <Schedule job={jobs[job]} />}
			</div>
		</div>
	);
};

const mapStateToProps = (state) => ({
	auth: state.auth,
	job: state.job,
	reservation: state.reservation,
});

export default connect(mapStateToProps, {
	loadJobs,
	loadReservations,
	updateLoadingSpinner,
})(Reservation);
