import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import Moment from 'react-moment';

import { loadUsers } from '../../actions/user';

import Schedule from '../Schedule';

import './ReservationForm.scss';

const ReservationForm = ({
	reservation,
	complete,
	jobId,
	job: { jobs },
	auth: { loggedUser },
	users: { users, loading },
	loadUsers,
}) => {
	const [adminValues, setAdminValues] = useState({
		job: jobId ? Number(jobId) : '',
		user: '',
		email: '',
		searchDisplay: false,
		results: [],
	});

	const { job, user, email, searchDisplay, results } = adminValues;

	useEffect(() => {
		if (reservation)
			setAdminValues((prev) => ({ ...prev, job: reservation.job.id }));
	}, [reservation]);

	return (
		<div className='reservation-form'>
			<div className='reservation-form__group'>
				<h4 className='heading-primary-subheading'>
					{reservation ? 'Update' : 'New'} Reservation
				</h4>
				{reservation && (
					<>
						<p className='heading-primary-subheading-update'>
							<Moment
								date={reservation.hourFrom}
								className='date'
								format='MM-DD-YY'
							/>{' '}
							&nbsp;
							<Moment date={reservation.hourFrom} utc format='h a' /> -{' '}
							<Moment date={reservation.hourTo} utc format='h a' />
						</p>
						{loggedUser.type === 'customer' && (
							<button
								className='reservation-form-cancel'
								onClick={() => {
									complete();
									setAdminValues((prev) => ({ ...prev, job: '' }));
								}}
							>
								<i className='fas fa-times'></i>
							</button>
						)}
					</>
				)}
				{loggedUser.type === 'admin' && (
					<div className='form__group form-search'>
						<input
							className='form__input'
							type='text'
							value={email}
							disabled={user !== ''}
							name='email'
							id='email'
							autoComplete='off'
							onFocus={() =>
								setAdminValues((prev) => ({
									...prev,
									searchDisplay: true,
								}))
							}
							onChange={(e) => {
								setAdminValues((prev) => ({
									...prev,
									email: e.target.value,
								}));
								if (e.target.value.length > 1)
									loadUsers(
										{
											type: 'customer',
											email: e.target.value,
										},
										true
									);
							}}
							placeholder="User's Email"
						/>
						<label htmlFor='email' className='form__label'>
							User's Email
						</label>

						{searchDisplay && email.length > 1 && user === '' && (
							<ul
								className={`form-search-display ${
									results.length === 0 ? 'danger' : ''
								}`}
							>
								{results.length > 0 ? (
									results.map((user) => (
										<li
											className='form-search-item'
											onClick={() => selectUser(user)}
											key={user._id}
										>
											{user.email}
										</li>
									))
								) : (
									<li className='bg-danger form-search-item'>
										No matching results
									</li>
								)}
							</ul>
						)}
						{(user !== '' || (email.length > 1 && user === '')) && (
							<button
								type='button'
								onClick={cancelUser}
								className='form-search-close'
							>
								<i className='fas fa-times'></i>
							</button>
						)}
					</div>
				)}
				<div className='form__group'>
					{!reservation && (
						<label htmlFor='job' className='form__label'>
							Select the Type of Job for the New Reservation:
						</label>
					)}

					<select
						className='form__input'
						id='job'
						value={job}
						disabled={reservation}
						onChange={(e) =>
							setAdminValues((prev) => ({ ...prev, job: e.target.value }))
						}
					>
						<option value=''>* Type of Job</option>
						{jobs.length > 0 &&
							jobs.map((job) => (
								<option key={job.id} value={job.id}>
									{job.title}
								</option>
							))}
					</select>
				</div>
			</div>
			{job !== '' && (
				<Schedule
					job={jobs[jobs.findIndex((item) => item.id === Number(job))]}
					complete={() => {
						setAdminValues((prev) => ({ ...prev, job: '' }));
						complete();
					}}
					reservation={reservation}
				/>
			)}
		</div>
	);
};

const mapStateToProps = (state) => ({
	job: state.job,
	auth: state.auth,
	user: state.user,
});

export default connect(mapStateToProps, { loadUsers })(ReservationForm);
