import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import Moment from 'react-moment';

import {
	loadReservations,
	deleteReservation,
} from '../../../actions/reservation';
import { loadUsers } from '../../../actions/user';
import { loadJobs } from '../../../actions/jobs';

import PopUp from '../../layouts/PopUp';
import Alert from '../../Alert';

import './ReservationsList.scss';

const ReservationsList = ({
	deleteReservation,
	loadReservations,
	loadUsers,
	loadJobs,
	user: { users, loading: loadingUsers },
	job: { jobs, loading: loadingJobs },
	reservation: { reservations, error, loading },
}) => {
	const [formData, setFormData] = useState({
		hourFrom: '',
		hourTo: '',
		user: '',
		job: '',
	});

	const [adminValues, setAdminValues] = useState({
		toggleDeleteConf: false,
		toDelete: '',
		email: '',
		searchDisplay: false,
		results: [],
	});

	const { hourFrom, hourTo, user, job } = formData;

	const { toggleDeleteConf, toDelete, email, searchDisplay, results } =
		adminValues;

	useEffect(() => {
		if (loading) {
			loadJobs(true);
			loadReservations({ hourFrom: new Date() }, true);
		}
	}, [loading, loadReservations]);

	const onChange = (e) => {
		setFormData((prev) => ({
			...prev,
			[e.target.id]: e.target.value,
		}));
	};

	const selectUser = (user) => {
		setAdminValues((prev) => ({
			...prev,
			email: user.email,
			results: [],
			searchDisplay: false,
		}));
		setFormData((prev) => ({
			...prev,
			user: user.id,
		}));
	};

	const cancelUser = () => {
		setAdminValues((prev) => ({
			...prev,
			email: '',
		}));
		setFormData((prev) => ({
			...prev,
			user: '',
		}));
	};

	const onSubmit = (e) => {
		e.preventDefault();
		loadReservations(formData);
	};

	return (
		<div className='reservations-list'>
			<PopUp
				type='confirmation'
				confirm={() => deleteReservation(toDelete)}
				setToggleModal={() =>
					setAdminValues((prev) => ({
						...prev,
						toggleDeleteConf: !toggleDeleteConf,
					}))
				}
				toggleModal={toggleDeleteConf}
				text='Are you sure you want to delete the reservation?'
			/>
			<h2 className='heading-primary'>Reservations</h2>

			<Alert type='1' />
			<form className='form filter' onSubmit={onSubmit}>
				<p className='filter-title'>Filter</p>
				<div className='form__group'>
					<div className='form__group-sub'>
						<div className='form__group-sub-item'>
							<input
								type='date'
								className='form__input'
								id='hourFrom'
								value={hourFrom}
								onChange={onChange}
								onFocus={() =>
									setAdminValues((prev) => ({
										...prev,
										searchDisplay: false,
									}))
								}
							/>
							<label htmlFor='hourFrom' className='form__label'>
								From
							</label>
						</div>
						<div className='form__group-sub-item'>
							<input
								type='date'
								className='form__input'
								id='hourTo'
								onChange={onChange}
								value={hourTo}
								onFocus={() =>
									setAdminValues((prev) => ({
										...prev,
										searchDisplay: false,
									}))
								}
							/>
							<label htmlFor='hourTo' className='form__label'>
								To
							</label>
						</div>
					</div>
				</div>
				<div className='form__group'>
					<label htmlFor='job' className='form__label'>
						Select the Type of Job:
					</label>

					<select
						className='form__input'
						id='job'
						value={job}
						onChange={onChange}
						onFocus={() =>
							setAdminValues((prev) => ({
								...prev,
								searchDisplay: false,
							}))
						}
					>
						<option value=''>* Type of Job</option>
						{jobs.length > 0 &&
							jobs.map((job, i) => (
								<option key={job.id} value={i}>
									{job.title}
								</option>
							))}
					</select>
				</div>
				<div className='form__group form-search'>
					<label htmlFor='email' className='form__label'>
						User's Email
					</label>
					<input
						className='form__input'
						type='text'
						value={email}
						disabled={user !== ''}
						name='email'
						id='email'
						onFocus={() =>
							setAdminValues((prev) => ({
								...prev,
								searchDisplay: true,
							}))
						}
						onChange={(e) =>
							setAdminValues((prev) => ({
								...prev,
								email: e.target.value,
							}))
						}
						placeholder="User's Email"
					/>

					{searchDisplay && email.length > 1 && user === '' && (
						<ul
							className={`form-search-display ${
								results.length === 0 ? 'danger' : ''
							}`}
						>
							{results.length > 0 ? (
								results.map((user) => (
									<li
										className='form-search-item'
										onClick={() => selectUser(user)}
										key={user._id}
									>
										{user.email}
									</li>
								))
							) : (
								<li className='bg-danger form-search-item'>
									No matching results
								</li>
							)}
						</ul>
					)}
					{(user !== '' || (email.length > 1 && user === '')) && (
						<button
							type='button'
							onClick={cancelUser}
							className='form-search-close'
						>
							<i className='fas fa-times'></i>
						</button>
					)}
				</div>

				<button type='submit' className='btn btn-tertiary'>
					<i className='fas fa-search'></i>
				</button>
			</form>
			{!loading && (
				<>
					{reservations.length > 0 ? (
						<div className='wrapper'>
							<table className='stick icon-6'>
								<thead>
									<tr>
										<th>Date</th>
										<th>From</th>
										<th>To</th>
										<th>User</th>
										<th>Job</th>
										<th></th>
										<th></th>
									</tr>
								</thead>
								<tbody>
									{reservations.map((reservation) => (
										<tr key={reservation.id}>
											<td>
												<Moment
													date={reservation.hourFrom}
													utc
													format='MM/DD/YY'
												/>
											</td>
											<td>
												<Moment date={reservation.hourFrom} utc format='h a' />
											</td>
											<td>
												<Moment date={reservation.hourTo} utc format='h a' />
											</td>
											<td>
												<Link
													className='btn-link text-dark'
													to={`/edit-user/${reservation.user.id}`}
												>{`${reservation.user.name} ${reservation.user.lastname}`}</Link>
											</td>
											<td>{reservation.job.title}</td>
											<td>
												<button
													className='btn-icon'
													onClick={() => {
														window.scroll(0, 0);
													}}
												>
													<i className='far fa-edit'></i>
												</button>
											</td>
											<td>
												<button
													className='btn-icon'
													onClick={() =>
														setAdminValues((prev) => ({
															...prev,
															toggleDeleteConf: !toggleDeleteConf,
															toDelete: reservation.id,
														}))
													}
												>
													<i className='far fa-trash-alt'></i>
												</button>
											</td>
										</tr>
									))}
								</tbody>
							</table>
							<div className='reservations-list-total'>
								<span className='heading-primary-subheading text-dark'>
									Total:
								</span>
								&nbsp;
								{reservations.length}
							</div>
						</div>
					) : (
						<h3 className='heading-primary text-danger'>{error.msg}</h3>
					)}
					<div className='btn-right'>
						<Link to='/new-reservation' className='btn btn-primary'>
							<i className='fas fa-plus'></i> &nbsp; Reservation
						</Link>
					</div>
				</>
			)}
		</div>
	);
};

const mapStateToProps = (state) => ({
	reservation: state.reservation,
	user: state.user,
	job: state.job,
});

export default connect(mapStateToProps, {
	loadReservations,
	deleteReservation,
	loadUsers,
	loadJobs,
})(ReservationsList);
